import { signupGoogleApi } from "../../Api/userApi"
export const updateUser = (
    {
        _id = '',
        email = '',
        password = '',
        givenName =  '',
        familyName = '',
        googleImageUrl='',        
    }={}
)=>({
    type:'UPDATE_USER',
    user:{
        _id,
        email,
        password,
        givenName,
        familyName,
        googleImageUrl,
    }
})

export const startCreateUser = (profile,token) =>{    
    (dispatch) =>{
        new Promise(function(resolve, reject) {        
        signupGoogleApi(profile,token).then(
            user =>{                
                dispatch(updateUser(user));
                resolve()
            }
        ).catch(
            err =>{
                console.log(err);
                reject(err);
            }
        )
    })
    }
}
