import {
  signupGoogleApi,
  signupApi,
  userSaveLocal,
  userFetchLocal,
  userRemoveLocal
} from "../../Api/userApi";

export const updateUser = ({
  _id = "",
  email = "",
  password = "",
  givenName = "",
  familyName = "",
  googleImageUrl = ""
} = {}) => ({
  type: "UPDATE_USER",
  user: {
    _id,
    email,
    password,
    givenName,
    familyName,
    googleImageUrl
  }
});

export const removeUser = () => ({
  type: "REMOVE_USER",
  user: {
    _id: "",
    email: "",
    password: ""
  }
});

export const startCreateUserGoogle = (profile, token) => {
  return (dispatch) => {
    return new Promise(function(resolve, reject) {
      signupGoogleApi(profile, token)
        .then((user) => {
          dispatch(updateUser(user));
          userSaveLocal(user);
          resolve(user);
        })
        .catch((err) => {
          console.log(err);
          reject(err);
        });
    });
  };
};

export const startCreateUserWebApi = (user) => {
  return (dispatch) => {
    return new Promise(function(resolve, reject) {
      signupApi(user)
        .then((user) => {
          dispatch(updateUser(user));
          userSaveLocal(user);
          resolve(user);
        })
        .catch((err) => {
          console.log(err);
          reject(err);
        });
    });
  };
};

export const startCreateUserLocal = (dispatch) => {
  const user = userFetchLocal();
  dispatch(updateUser(user));
  return user;
};

export const startRemoveUserLocal = (dispatch) => {
  dispatch(removeUser());
  userRemoveLocal();
};
