import React from 'react';
import CourseContainer from './Course-Container'
import Orientation from 'react-native-orientation'

export default class  CourseContainer2 extends React.Component{
  constructor(props){
      super(props);
      this.state={
          height:0
      }
  }
  _orientationDidChange(orientation) {
    console.log(orientation)
  }

  componentDidMount() {
    setTimeout(()=>{
        this.height = document.getElementById("video").offsetHeight;        
        this.setState({
            height: this.height
          });
    }, 500);
    Orientation.addOrientationListener(this._orientationDidChange);
  }
  render() {
    return <CourseContainer height={this.state.height }></CourseContainer>;
  }
}
