import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import Divider from "@material-ui/core/Divider";
import TextField from "@material-ui/core/TextField";
import Box from "@material-ui/core/Box";
import LinearProgress from "@material-ui/core/LinearProgress";
import Grid from "@material-ui/core/Grid";
import { GoogleLogin } from "react-google-login";
import { startCreateUser } from "../Redux/Actions/user";
import { connect } from 'react-redux';

const useStyles = makeStyles(theme => ({
  dialog: {
    minHeight:"80vh" // 80 percent of screen height
  },
  dialogContent:{
    marginTop:15 // 15 * 1 pixel
  }
}))

const renderTextField = (
  { input, label, meta: { touched="", error }, ...custom },
) => (
  <TextField
    label={label}
    fullWidth
    id="givenName"                  
    helperText=""
    margin="dense"
    variant="outlined"      
    errorText={touched && error}
    {...input}
    {...custom}
  />
);

function LoginSignupDialog(props) {
  const { handleSubmit, pristine, reset, submitting } = props;
  const [progressVisible,setProgressVisible] = React.useState(false);
  const classes = useStyles();  
  const responseGoogle = response => {
    var profile = response.getBasicProfile();
    console.log("ID: " + profile.getId()); // Don't send this directly to your server!
    console.log('Full Name: ' + profile.getName());
    console.log('Given Name: ' + profile.getGivenName());
    console.log('Family Name: ' + profile.getFamilyName());
    console.log("Image URL: " + profile.getImageUrl());
    console.log("Email: " + profile.getEmail());          
    props.dispatch(startCreateUser(profile,response.getAuthResponse().id_token)).then(
      (user)=>{
        props.closeDialog();
        setProgressVisible(false)
      }
    ).catch(
      (err)=>{        
        setProgressVisible(false)
        props.onError("Bir hata meydana geldi.");        
      }
    ) 
  };

  return (
    <div>
      <Dialog
        open={props.isActive}
        onClose={props.closeDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
        className={classes.dialog}        
      >          
      <img style={{ width: "100%" }} src="/banner.jpg"></img>            
        {progressVisible? <LinearProgress variant="query" color="secondary" /> :null }        
        <DialogContent className={classes.dialogContent}>        
          <Grid container justify="center" spacing={2}>                        
            <GoogleLogin 
                onRequest={(e)=>{                 
                  setProgressVisible(true)                  
                }}               
                clientId="152160269393-ko1pdpt8sr5gdqfnhvhbon3u7sh7qpe4.apps.googleusercontent.com"
                buttonText="Google ile kayıt ol"                
                onSuccess={responseGoogle}
                onFailure={responseGoogle}
                cookiePolicy={"single_host_origin"}
                theme="dark"              
              />            
            <Grid item xs={12}>
              <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
              >
                <Grid item xs={4}>
                  <Divider />
                </Grid>
                <Grid item xs={4}>
                  <Box textAlign="center" m={1}>
                    veya email ile kayıt ol
                  </Box>
                </Grid>
                <Grid item xs={4}>
                  <Divider />
                </Grid>
              </Grid>
            </Grid>
            <form onSubmit={handleSubmit}>
            <Grid container justify="center" item spacing={6}>
              <Grid  item justify="center" xs={6}>
                <TextField
                  label="İsim"
                  name= "givenName"
                  component={renderTextField}
                />
              </Grid>
              <Grid  item justify="center" xs={6}>
              <TextField
                  error
                  label="Soyisim"
                  fullWidth
                  id="familyName"                  
                  helperText=""
                  margin="dense"
                  variant="outlined"
                />
              </Grid>
            </Grid>
            <Grid container justify="center" item spacing={6}>
              <Grid item justify="center" xs={6}>
              <TextField
                  error
                  label="Email"
                  fullWidth
                  id="email"                  
                  helperText=""
                  margin="dense"
                  variant="outlined"
                />
              </Grid>
              <Grid item justify="center" xs={6}>
              <TextField
                  error
                  label="Şifre"
                  type="password"
                  fullWidth
                  id="password"                  
                  helperText=""
                  margin="dense"
                  variant="outlined"
                />
              </Grid>
            </Grid>
            <Grid item xs={12}>
              <Box textAlign="center" m={0}>
                Üye olarak gizlilik sözleşmemizi ve Kullanım Şartlarımızı kabul
                etmiş olursunuz.
              </Box>
            </Grid>
            <Grid item xs={12}>
              <Box textAlign="center" m={0}>
                <Button
                  style={{ width: "100%" }}
                  onClick={props.closeDialog}
                  variant="contained"
                  color="secondary"
                >
                  Üye Ol
                </Button>
              </Box>
            </Grid>
            </form>
            <Grid container justify="center" spacing={0}>
              <Grid item xs={6}>
                <Box textAlign="right" m={1}>
                  Üye misiniz?
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Button onClick={props.closeDialog}>Giriş yapın</Button>
              </Grid>
            </Grid>
          </Grid>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default connect()(LoginSignupDialog)
