import React from "react";
import PropTypes from "prop-types";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Divider from "@material-ui/core/Divider";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import { NavLink, BrowserRouter } from "react-router-dom";
import Avatar from "@material-ui/core/Avatar";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  sideMenu: {}
}));

function SideMenu(props) {
  const classes = useStyles();
  const [stateMenu, setStateMenu] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false
  });

  const toggleDrawer = (side, open) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }
    console.log("toggleDrawer");
    console.log({ ...stateMenu, [side]: open });
    setStateMenu({ ...stateMenu, [side]: open });
    props.open(false);
  };

  return (
    <Drawer
      anchor="right"
      open={props.open}
      onClose={toggleDrawer("right", false)}
    >
      <div
        className={classes.sideMenu}
        role="presentation"
        onClick={toggleDrawer(props.type, false)}
        onKeyDown={toggleDrawer(props.type, false)}
      >
        <List>
          <ListItem component={NavLink} to="/user" button key="1">
            <ListItemAvatar>
              <Avatar
                alt="Remy Sharp"
                src="https://experience.sap.com/fiori-design-web/wp-content/uploads/sites/5/2017/02/Avatar-Sizes-Custom-1.png"
              />
            </ListItemAvatar>
            <ListItemText
              primary="Çağlayan Şerbetçi"
              secondary="caglayanserbetci@gmail.com"
            />
          </ListItem>
          <ListItem component={NavLink} to="/" button key="2">
            <ListItemText primary="Dersim" />
          </ListItem>
          <ListItem component={NavLink} to="/user/account" button key="2">
            <ListItemText primary="Hesabım" />
          </ListItem>
          <Divider />
          <ListItem component={NavLink} to="/help" button key="2">
            <ListItemText primary="Yardım" />
          </ListItem>
          <ListItem button key="1">
            <ListItemText primary="Çıkış" />
          </ListItem>
        </List>
      </div>
    </Drawer>
  );
}

SideMenu.propTypes = {};

export default SideMenu;
