import React from "react";
import { Formik } from "formik";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import { connect } from "react-redux";

const useStyles = makeStyles((theme) => ({
  Avatar: {
    width: 150,
    height: 150
  }
}));
const AvatarForm = (props) => {
  const classes = useStyles();
  console.log("Hey");
  console.log(props.givenName);
  return (
    <Formik
      enableReinitialize={true}
      initialValues={{
        givenName: props.givenName,
        familyName: props.familyName,
        email: props.email,
        password: props.password
      }}
      validate={(values) => {
        const errors = {};
        if (!values.email) {
          errors.email = "Doldurulması zorunludur.";
        } else if (
          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
          errors.email = "Doğru bir email adresi değil gibi.";
        }
        return errors;
      }}
      onSubmit={(values, { setSubmitting }) => {
        props.onSubmit(values);
      }}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting
        /* and other goodies */
      }) => (
        <form style={{ width: "100%" }} onSubmit={handleSubmit}>
          <Typography variant="h5">Profil Resmini Güncelle</Typography>
          <Typography variant="subtitle2">
            Profil resminizi güncelleyebilirsiniz.
          </Typography>
          <Grid
            container
            style={{ marginTop: "10px" }}
            justify="center"
            item
            spacing={6}
          >
            <Avatar
              onClick={props.sideMenuOpen}
              className={classes.Avatar}
              style={{ marginTop: "20px" }}
              alt={props.givenName + " " + props.familyName}
              src={props.avatarImageUrl}
            >
              {props.givenName ? props.givenName.charAt(0).toUpperCase() : null}
              {props.familyName
                ? props.familyName.charAt(0).toUpperCase()
                : null}
            </Avatar>
            <Box textAlign="center" m={0}>
              <Button
                style={{ width: "100%" }}
                onClick={props.closeDialog}
                variant="contained"
                color="secondary"
                type="submit"
                disabled={isSubmitting}
              >
                Güncelle
              </Button>
            </Box>
          </Grid>
          <Grid item style={{ marginTop: "25px" }} xs={6}>
            <Box textAlign="center" m={0}>
              <Button
                style={{ width: "100%" }}
                onClick={props.closeDialog}
                variant="contained"
                color="secondary"
                type="submit"
                disabled={isSubmitting}
              >
                Güncelle
              </Button>
            </Box>
          </Grid>
        </form>
      )}
    </Formik>
  );
};

const AvatarFormCon = connect((state) => {
  return {
    _id: state.userReducer._id,
    givenName: state.userReducer.givenName,
    familyName: state.userReducer.familyName,
    avatarImageUrl: state.userReducer.avatarImageUrl,
    email: state.userReducer.email
  };
})(AvatarForm);

export default AvatarFormCon;
