import React from 'react';
import './App.css';
import {createMuiTheme,ThemeProvider,CssBaseline,Divider} from '@material-ui/core'
import { BrowserRouter } from "react-router-dom";
import { Provider } from "react-redux"
import BodyArea from './Layout/BodyArea';
import Header from './Layout/Header'
import Footer from './Layout/Footer'
import LoginSignupDialog from  './Dialogs/LoginSignupDialog'
import configureStore from './Redux/Store/configStore'

const store = configureStore();

console.log(store.getState());

const theme = createMuiTheme({
  palette:{
    type:'dark'
  },
  typography: {
    button: {
      textTransform: 'none'
    }
  }
});

export default function App(){
  const [open,setOpen, isLogin, setisLogin] = React.useState(false);

  React.useEffect(() => {
    axios
      .get(
        "https://gist.githubusercontent.com/witalewski/fc8f043d53a0d505f84c5ddb04ae76ea/raw/7c505bbc1675a0bc8a067f8b633b531c769bb64c/data.json"
      )
      .then(({ data }) => {
        console.log(data)
      }).catch( err =>{
        console.log(err)
      });
  }, []);



  const handleClickLoginOpen = () => {
    console.log("login button is clicked")
    setOpen(true)
  };

  const handleClickSignupOpen = () => {
    console.log("signup button is clicked")    
    setOpen(false)
  };

  const handleCloseDialog = () =>{
    console.log("close dialog")  
    setOpen(false)
    setisLogin(true)
  }


  return(
    <div className="App">
      <BrowserRouter>
        <Provider store={store}>
          <ThemeProvider theme={theme}>
          <CssBaseline />
          <Header isUserLogin ={isLogin} loginOpen={handleClickLoginOpen} signupOpen={handleClickSignupOpen}/>
          <Divider />
          <BodyArea/>    
          <Footer/>
          <LoginSignupDialog closeDialog={handleCloseDialog} isActive={true}/>  
          </ThemeProvider> 
        </Provider>
      </BrowserRouter>   
    </div>
  );
}
