import React from "react";
import { Switch, Route, NavLink, Redirect } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import List from "@material-ui/core/List";
import Card from "@material-ui/core/Card";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListSubheader from "@material-ui/core/ListSubheader";
import { connect } from "react-redux";
import { startRemoveUserLocal } from "../Redux/Selectors/userSelector";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(6),
    marginBottom: theme.spacing(8),
    minHeight: "54vh",
    [theme.breakpoints.down("sm")]: {
      marginTop: theme.spacing(3)
    }
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  Avatar: {
    width: "auto",
    height: 90
  },
  ListItemActive: {
    backgroundColor: theme.palette.action.selected
  },
  card: {
    maxWidth: 345
  }
}));
const Account = (props) => {
  const classes = useStyles();

  const logoutUser = () => {
    console.log("user logout");
    startRemoveUserLocal(props.dispatch);
    props.history.push("/");
  };

  return (
    <Container className={classes.container} maxWidth="lg">
      <Grid
        direction="row"
        justify="center"
        alignItems="center"
        container
        xs={12}
        spacing={2}
      >
        <Card className={classes.card}>
          <CardMedia
            className={classes.media}
            image={props.shoppingCart ? props.shoppingCart[0].img : null}
            title={props.shoppingCart ? props.shoppingCart[0].title : null}
          />
          <CardContent>
            <Typography variant="body2" color="textSecondary" component="p">
              This impressive paella is a perfect party dish and a fun meal to
              cook together with your guests. Add 1 cup of frozen peas along
              with the mussels, if you like.
            </Typography>
          </CardContent>
        </Card>
        <List component="nav" aria-label="main shoppingcart item">
          <ListItem button>
            <Avatar
              variant="rounded"
              onClick={props.sideMenuOpen}
              className={classes.Avatar}
              alt={props.shoppingCart ? props.shoppingCart[0].title : null}
              src={props.shoppingCart ? props.shoppingCart[0].img : null}
            >
              {props.givenName ? props.givenName.charAt(0).toUpperCase() : null}
              {props.familyName
                ? props.familyName.charAt(0).toUpperCase()
                : null}
            </Avatar>
            <ListItemText
              primary={props.shoppingCart ? props.shoppingCart[0].title : null}
              secondary="Jan 9, 2014"
            />
          </ListItem>
        </List>
      </Grid>
    </Container>
  );
};

const AccountCon = connect((state) => {
  return {
    _id: state.userReducer._id,
    shoppingCart: state.userReducer.shoppingCart
  };
})(Account);

export default AccountCon;
