import React from "react";
import { Formik } from "formik";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Avatar from "@material-ui/core/Avatar";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import List from "@material-ui/core/List";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import { NavLink } from "react-router-dom";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListSubheader from "@material-ui/core/ListSubheader";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(6),
    marginBottom: theme.spacing(8),
    minHeight: "50vh",
    [theme.breakpoints.down("sm")]: {
      marginTop: theme.spacing(3)
    }
  },
  Avatar: {
    width: 100,
    height: 100
  },
  ListItemActive: {
    backgroundColor: theme.palette.action.selected
  }
}));
const Account = (props) => {
  const classes = useStyles();
  return (
    <Container className={classes.container} maxWidth="lg">
      <Grid container xs={12} spacing={2}>
        <Grid container justify="center" xs={4} spacing={2}>
          <List
            minWidth="75%"
            subheader={
              <ListSubheader component="div" id="nested-list-subheader">
                <ListItemAvatar>
                  <Avatar
                    onClick={props.sideMenuOpen}
                    className={classes.Avatar}
                    alt={props.givenName + " " + props.familyName}
                    src={props.avatarImageUrl}
                  ></Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={
                    props.givenName
                      ? props.givenName + " " + props.familyName
                      : "bilinmiyor"
                  }
                ></ListItemText>
              </ListSubheader>
            }
          >
            <ListItem
              component={NavLink}
              exact
              to="/user/account"
              activeClassName={classes.ListItemActive}
              button
              key="account"
            >
              <ListItemText primary="Hesabım" />
            </ListItem>
            <ListItem
              component={NavLink}
              exact
              to="/user/avatar"
              activeClassName={classes.ListItemActive}
              button
              key="avatar"
            >
              <ListItemText primary="Profil Resmi" />
            </ListItem>
            <ListItem
              component={NavLink}
              exact
              to="/user/password"
              activeClassName={classes.ListItemActive}
              button
              key="password"
            >
              <ListItemText primary="Şifremi değiştir" />
            </ListItem>
            <ListItem onClick={props.logoutUser} button key="25">
              <ListItemText primary="Çıkış" />
            </ListItem>
          </List>
        </Grid>
        <Grid container alignItems="center" xs={8} spacing={2}>
          <Formik
            initialValues={{
              givenName: "",
              familyName: "",
              email: "",
              password: ""
            }}
            validate={(values) => {
              const errors = {};
              if (!values.email) {
                errors.email = "Doldurulması zorunludur.";
              } else if (
                !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
              ) {
                errors.email = "Doğru bir email adresi değil gibi.";
              }
              return errors;
            }}
            onSubmit={(values, { setSubmitting }) => {
              props.onSubmit(values);
            }}
          >
            {({
              values,
              errors,
              touched,
              handleChange,
              handleBlur,
              handleSubmit,
              isSubmitting
              /* and other goodies */
            }) => (
              <form style={{ width: "100%" }} onSubmit={handleSubmit}>
                <Grid container justify="center" item spacing={6}>
                  <Grid item xs={6}>
                    <TextField
                      label="İsim"
                      fullWidth
                      name="givenName"
                      id="givenName"
                      helperText=""
                      margin="dense"
                      variant="outlined"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.givenName}
                    />
                  </Grid>
                  <Grid item xs={6}>
                    <TextField
                      label="Soyisim"
                      fullWidth
                      name="familyName"
                      id="familyName"
                      helperText=""
                      margin="dense"
                      variant="outlined"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.familyName}
                    />
                  </Grid>
                </Grid>
                <Grid container justify="center" item spacing={6}>
                  <Grid item xs={6}>
                    <TextField
                      error={errors.email != null && touched.email}
                      label="Email"
                      fullWidth
                      id="email"
                      helperText={errors.email && touched.email && errors.email}
                      margin="dense"
                      variant="outlined"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.email}
                    />
                  </Grid>

                  <Grid item xs={6}>
                    <TextField
                      error={errors.password != null}
                      label="Şifre"
                      type="password"
                      fullWidth
                      id="password"
                      helperText={
                        errors.password && touched.password && errors.password
                      }
                      margin="dense"
                      variant="outlined"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.password}
                    />
                  </Grid>
                </Grid>
                <Grid item style={{ marginTop: "15px" }} xs={12}>
                  <Box textAlign="center" m={0}>
                    Üye olarak gizlilik sözleşmemizi ve Kullanım Şartlarımızı
                    kabul etmiş olursunuz.
                  </Box>
                </Grid>
                <Grid item style={{ marginTop: "15px" }} xs={12}>
                  <Box textAlign="center" m={0}>
                    <Button
                      style={{ width: "100%" }}
                      onClick={props.closeDialog}
                      variant="contained"
                      color="secondary"
                      type="submit"
                      disabled={isSubmitting}
                    >
                      Üye Ol
                    </Button>
                  </Box>
                </Grid>
              </form>
            )}
          </Formik>
        </Grid>
      </Grid>
    </Container>
  );
};

const AccountCon = connect((state) => {
  return {
    _id: state.userReducer._id,
    givenName: state.userReducer.givenName,
    familyName: state.userReducer.familyName,
    avatarImageUrl: state.userReducer.avatarImageUrl,
    email: state.userReducer.email
  };
})(Account);

export default AccountCon;
