import React from 'react';
import './App.css';
import {createMuiTheme,ThemeProvider,CssBaseline,Divider} from '@material-ui/core'
import { BrowserRouter } from "react-router-dom";
import { Provider } from "react-redux"
import BodyArea from './Layout/BodyArea';
import Header from './Layout/Header'
import Footer from './Layout/Footer'
import SnackBar from './Dialogs/SnackBar'
import LoginSignupDialog from  './Dialogs/LoginSignupDialog'
import configureStore from './Redux/Store/configStore'
import { userFetchLocal } from './Api/userApi'

const store = configureStore();

store.subscribe(()=>{
  const state = store.getState();
  console.log(state);
})

const theme = createMuiTheme({
  palette:{
    type:'dark'
  },
  typography: {
    button: {
      textTransform: 'none'
    }
  }
});

export default function App(){  
  const [loginIsActive,setLoginIsActive] = React.useState(false);
  const [snackIsActive,setSnackIsActive] = React.useState(false);
  var snackType = 2, snackMessage = "Sorun";

  React.useEffect(() => {
    userFetchLocal() ? setLoginIsActive(true) : setLoginIsActive(false);
  },[]);

  const handleClickLoginOpen = () => {
    console.log("login button is clicked")
    setLoginIsActive(true)
    setSnackIsActive(true)
  };

  const handleClickSignupOpen = () => {
    console.log("signup button is clicked")    
    setLoginIsActive(true)
    setSnackIsActive(true)
  };

  const handleCloseDialog = () =>{
    console.log("close dialog")  
    setLoginIsActive(false)
  }

  const handleCloseSnack = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }    
    setSnackIsActive(false)
  };

  const handleDialogError = (message)=>{
    setSnackIsActive(true)
    snackType = 2
    snackMessage = message
  }



  return(
    <div className="App">
      <BrowserRouter>
        <Provider store={store}>
          <ThemeProvider theme={theme}>
          <CssBaseline />
          <Header isUserLogin ={false} loginOpen={handleClickLoginOpen} signupOpen={handleClickSignupOpen}/>
          <Divider />
          <BodyArea/>    
          <Footer/>
          <LoginSignupDialog closeDialog={handleCloseDialog} isActive={loginIsActive} onError={handleDialogError}/> 
          <SnackBar type={snackType} message={snackMessage} closeSnack={handleCloseSnack} isActive={snackIsActive}/> 
          </ThemeProvider> 
        </Provider>
      </BrowserRouter>   
    </div>
  );
}
