import React from 'react';
import CourseContainer from './Course-Container'


export default class  CourseContainer2 extends React.Component{
  constructor(props){
      super(props);
      this.state={
          height:0
      }
  }

  componentDidMount() {
    setTimeout(()=>{
        this.height = document.getElementById("video").offsetHeight;        

    }, 500);    
  }

  onResize = (width,height) => {
    console.log(width,height)
    this.setState({
      height: height
    });

  }


  render() {
    return (
    <CourseContainer onResize={this.onResize } height={this.state.height}></CourseContainer>)
    ;
  }
}
