import React from 'react';
import CourseContainer from './Course-Container'
import ReactResizeDetector from 'react-resize-detector';

export default class  CourseContainer2 extends React.Component{
  constructor(props){
      super(props);
      this.state={
          height:0
      }
  }

  componentDidMount() {
    setTimeout(()=>{
        this.height = document.getElementById("video").offsetHeight;        
        this.setState({
            height: this.height
          });
    }, 500);    
  }

  onResize = () => {
    console.log('resize')
  }

  render() {
    return (
      <ReactResizeDetector handleWidth handleHeight>
      {({ width, height }) => <div>{`${width}x${height}`}</div>}
    </ReactResizeDetector>
    )
    ;
  }
}
