import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import VideoPlayer from "../HelperComponents/VideoPlayer";
import ReactResizeDetector from "react-resize-detector";
import Chip from "@material-ui/core/Chip";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Collapse from "@material-ui/core/Collapse";
import ExpandLess from "@material-ui/icons/ExpandLess";
import ExpandMore from "@material-ui/icons/ExpandMore";
import { connect } from "react-redux";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import StarBorder from "@material-ui/icons/StarBorder";

/*
value         |0px     600px    960px    1280px   1920px
key           |xs      sm       md       lg       xl
screen width  |--------|--------|--------|--------|-------->
range         |   xs   |   sm   |   md   |   lg   |   xl
*/

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(6),
    marginBottom: theme.spacing(8),
    [theme.breakpoints.down("sm")]: {
      marginTop: theme.spacing(3)
    }
  },
  courseName: {
    paddingInlineEnd: "10px",
    display: "inline-flex",
    marginBottom: "0px",
    [theme.breakpoints.down("sm")]: {
      fontSize: "1rem",
      borderRight: "none"
    }
  },
  courseType: {
    borderLeft: "0.05em solid white",
    paddingInlineStart: "10px",
    display: "inline-flex",
    marginBottom: "0px",
    [theme.breakpoints.down("sm")]: {
      fontSize: "1rem",
      display: "none"
    }
  },
  CourseInstName: {
    paddingInlineEnd: "10px",
    margin: "0px",
    display: "inline-flex",
    [theme.breakpoints.down("sm")]: {
      fontSize: "0.8rem"
    }
  },
  followButton: {
    margin: "0px",
    paddingInlineStart: "10px",
    display: "inline-flex",
    [theme.breakpoints.down("sm")]: {
      fontSize: "0.8rem"
    }
  },
  grid: {
    marginTop: theme.spacing(1),
    display: "flex",
    alignItems: "stretch",
    width: "100%"
  },
  divider: {
    backgroundColor: theme.palette.background.paper,
    height: "60px"
  },
  tabs: {
    width: "100%",
    backgroundColor: theme.palette.background.paper
  },
  root: {
    width: "100%"
  },
  nested: {
    paddingLeft: theme.spacing(4)
  }
}));

const CourseVideoContent = (props) => {
  const classes = useStyles();
  const [state, setState] = React.useState({ shown: { 0: true } });

  const expandChapters = (index) => {
    setState({
      shown: {
        ...state.shown,
        [index]: !state.shown[index]
      }
    });
  };

  const msToTime = (duration) => {
    let seconds = Math.floor(duration % 60),
      minutes = Math.floor((duration / 60) % 60),
      hours = Math.floor((duration / (60 * 60)) % 24);
    seconds = seconds < 10 ? "0" + seconds : seconds;
    let str = "";
    if (hours > 0) {
      str = "(" + str + hours + " saat " + minutes + " dakika" + ")";
    } else if (minutes > 0) {
      str = "(" + str + minutes + " dakika" + ")";
    }
    return str;
  };

  const CourseListItem = (props) => {
    if (!props.chapters) {
      return <p>yükleniyor..</p>;
    }
    return props.chapters.map((chapter, index) => {
      return (
        <div>
          <ListItem
            key={index}
            button
            onClick={() => {
              return handleClick(index);
            }}
          >
            <ListItemIcon>
              <InboxIcon />
            </ListItemIcon>
            <ListItemText primary="Inbox" />
            {open.shown[index] ? <ExpandLess /> : <ExpandMore />}
          </ListItem>
          <Collapse in={open.shown[index]} timeout="auto" unmountOnExit>
            <List component="div" disablePadding>
              <ListItem button className={classes.nested}>
                <ListItemIcon>
                  <StarBorder />
                </ListItemIcon>
                <ListItemText primary="Starred" />
              </ListItem>
            </List>
          </Collapse>
        </div>
      );
    });
  };

  const ListCourse = (props) => {
    if (!props.chapters) {
      return <p>yükleniyor..</p>;
    }
    return props.chapters.map((chapter, index) => {
      return (
        <div>
          <ListItem
            button
            key={chapter._id}
            onClick={() => {
              return expandChapters(index);
            }}
          >
            <ListItemText primary={index + 1 + ". " + chapter.title} />
            {state.shown[chapter._id] ? <ExpandLess /> : <ExpandMore />}
          </ListItem>
          <ListCollapseItem index={index} sections={chapter.sections} />
        </div>
      );
    });
  };

  const CourseContentList = (props) => {
    return (
      <Collapse in={state.shown[props.index]} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          {props.sections.map((section) => {
            return (
              <ListItem button key={section._id} className={classes.nested}>
                <ListItemText primary={section.title} />
              </ListItem>
            );
          })}
        </List>
      </Collapse>
    );
  };
  const [open, setOpen] = React.useState({ shown: { 0: true } });

  const handleClick = (index) => {
    setOpen({
      shown: {
        ...open.shown,
        [index]: !open.shown[index]
      }
    });
  };

  const index = 0;
  return (
    <Container className={classes.container} maxWidth="lg">
      <List>
        {props.chapters
          ? props.chapters.map((chapter, index) => {
              return (
                <div key={index}>
                  <ListItem
                    button
                    onClick={() => {
                      return handleClick(index);
                    }}
                  >
                    <ListItemText primary={index + 1 + ". " + chapter.title} />
                    {open.shown[index] ? <ExpandLess /> : <ExpandMore />}
                  </ListItem>
                  <Collapse in={open.shown[index]} timeout="auto" unmountOnExit>
                    <List component="div" disablePadding>
                      {chapter.sections.map((section) => {
                        return (
                          <ListItem
                            button
                            key={section._id}
                            className={classes.nested}
                          >
                            <ListItemText primary={section.title} />
                          </ListItem>
                        );
                      })}
                    </List>
                  </Collapse>
                </div>
              );
            })
          : null}
      </List>

      <Typography variant="h5" component="h1">
        <Box className={classes.courseName} fontWeight="fontWeightBold">
          {props.title}
        </Box>
        {props.isBelongMiuul == true ? (
          <Box className={classes.courseType} fontWeight="fontWeightBold">
            Original
          </Box>
        ) : null}
      </Typography>
      <Typography variant="subtitle1" component="h1">
        <Box className={classes.CourseInstName} fontWeight="fontWeightLight">
          {props.instructor
            ? props.instructor.givenName + " " + props.instructor.familyName
            : null}
        </Box>
        <Box className={classes.followButton} fontWeight="fontWeightBold"></Box>
      </Typography>
      <Grid container className={classes.grid} spacing={1}>
        <Grid
          id="video"
          style={{ height: "100%", overflow: "hidden", whiteSpace: "nowrap" }}
          item
          sm={8}
          xs={12}
        >
          <ReactResizeDetector
            handleWidth
            handleHeight
            onResize={props.onResize}
          />
          <VideoPlayer></VideoPlayer>
        </Grid>
        <Grid
          className={classes.gridChild}
          style={{
            height: props.height + "px",
            overflow: "auto",
            whiteSpace: "nowrap"
          }}
          item
          sm={4}
          xs={12}
        >
          <List component="nav" className={classes.root}>
            <ListItem key="112233">
              <Box
                className={classes.CourseInstName}
                fontWeight="fontWeightLight"
              >
                <Typography variant="subtitle1">
                  {props.numberOfSections} Ders {msToTime(props.duration)}
                </Typography>
              </Box>
            </ListItem>
            <ListCourse key="112234" chapters={props.chapters}></ListCourse>
          </List>
        </Grid>
      </Grid>
      <Grid container spacing={2}>
        <Grid item>
          Bu eğitimdeki yetenekler:
          <Chip style={{ marginLeft: 8 }} label="#Python" variant="outlined" />
          <Chip
            style={{ marginLeft: 8 }}
            label="#Veri Görselleştirme"
            variant="outlined"
          />
          <Chip
            style={{ marginLeft: 8 }}
            label="#Metin Madenciliği"
            variant="outlined"
          />
        </Grid>
      </Grid>
    </Container>
  );
};

const CourseVideoContentCon = connect((state) => {
  const settings = [];
  if (state.courseReducer.chapters) {
    state.courseReducer.chapters.map((chapter) => {
      settings.push({
        _id: chapter._id,
        open: false
      });
    });
  }
  return {
    _id: state.courseReducer._id,
    title: state.courseReducer.title,
    isBelongMiuul: state.courseReducer.isBelongMiuul,
    instructor: state.courseReducer.instructor,
    description: state.courseReducer.description,
    duration: state.courseReducer.duration,
    numberOfSections: state.courseReducer.numberOfSections,
    chapters: state.courseReducer.chapters,
    settings: settings /*state.courseReducer.chapters.map((chapter) => {
      return { _id: chapter._id, open: false };
    })*/
  };
})(CourseVideoContent);

export default CourseVideoContentCon;
