import React from "react";
import { Formik } from "formik";
import TextField from "@material-ui/core/TextField";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import { connect } from "react-redux";

const useStyles = makeStyles((theme) => ({}));

const PasswordForm = (props) => {
  const classes = useStyles();
  console.log("Hey");
  console.log(props.givenName);
  return (
    <Formik
      enableReinitialize={true}
      initialValues={{
        pass1: props.password,
        pass2: props.password
      }}
      validate={(values) => {
        console.log(values);
        const errors = {};
        if (!values.pass1) {
          errors.pass1 = "Doldurulması zorunludur.";
        } else if (
          !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
          errors.email = "Doğru bir email adresi değil gibi.";
        }
        return errors;
      }}
      onSubmit={(values, { setSubmitting }) => {
        props.onSubmit(values);
      }}
    >
      {({
        values,
        errors,
        touched,
        handleChange,
        handleBlur,
        handleSubmit,
        isSubmitting
        /* and other goodies */
      }) => (
        <form style={{ width: "100%" }} onSubmit={handleSubmit}>
          <Typography variant="h5">Şifre Değiştir</Typography>
          <Typography variant="subtitle2">
            Şifrenizi değiştirebilirsiniz.
          </Typography>
          <Grid
            container
            style={{ marginTop: "10px" }}
            justify="center"
            item
            spacing={2}
          >
            <Grid item xs={12}>
              <TextField
                label="Şifre"
                fullWidth
                name="pass1"
                type="password"
                id="pass1"
                helperText=""
                margin="dense"
                variant="outlined"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.pass1}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Şifrenizi tekrar yazınız"
                fullWidth
                name="pass2"
                id="pass2"
                type="password"
                helperText=""
                margin="dense"
                variant="outlined"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.pass2}
              />
            </Grid>
          </Grid>
          <Grid item style={{ marginTop: "25px" }} xs={12}>
            <Box textAlign="center" m={0}>
              <Button
                style={{ width: "100%" }}
                onClick={props.closeDialog}
                variant="contained"
                color="secondary"
                type="submit"
                disabled={isSubmitting}
              >
                Güncelle
              </Button>
            </Box>
          </Grid>
        </form>
      )}
    </Formik>
  );
};

const PasswordFormCon = connect((state) => {
  return {
    _id: state.userReducer._id,
    password: state.userReducer.password
  };
})(PasswordForm);

export default PasswordFormCon;
