import React from "react";
import { Player, ControlBar } from "video-react";

const VideoPlayerComponent = (props) => {
  /*
  let player = null;
  React.useEffect(
    (changed) => {
      console.log(props.currentSources);
      if (player) {
        player.source = props.currentSources;
        player.play();
      }
    },
    [props.currentSources]
  );

  React.useEffect(() => {
    console.log("componentDidMount");
    player = new plyr(".js-plyr", props.options);
    player.source = props.sources;

    return () => {
      console.log("componentWillUnmount");
      this.player.destroy();
    };
  }, []);
*/
  return (
    <Player
      fluid={false}
      height={500}
      autoPlay
      src="https://media.w3.org/2010/05/sintel/trailer_hd.mp4"
    >
      <ControlBar autoHide={false} className="my-class" />
    </Player>
  );
};

export default VideoPlayerComponent;
