import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogContent from "@material-ui/core/DialogContent";
import Divider from "@material-ui/core/Divider";
import Box from "@material-ui/core/Box";
import LinearProgress from "@material-ui/core/LinearProgress";
import Grid from "@material-ui/core/Grid";
import { GoogleLogin } from "react-google-login";
import {
  startCreateUserGoogle,
  startCreateUserWebApi
} from "../Redux/Actions/userActions";
import { connect } from "react-redux";
import MaterialUiForm from "./SignupForm";

const useStyles = makeStyles((theme) => ({
  dialog: {
    minHeight: "80vh" // 80 percent of screen height
  },
  dialogContent: {
    marginTop: 15 // 15 * 1 pixel
  }
}));

function LoginSignupDialog(props) {
  const [progressVisible, setProgressVisible] = React.useState(false);
  const classes = useStyles();
  const responseGoogle = (response) => {
    var profile = response.getBasicProfile();
    console.log("ID: " + profile.getId()); // Don't send this directly to your server!
    console.log("Full Name: " + profile.getName());
    console.log("Given Name: " + profile.getGivenName());
    console.log("Family Name: " + profile.getFamilyName());
    console.log("Image URL: " + profile.getImageUrl());
    console.log("Email: " + profile.getEmail());
    props
      .dispatch(
        startCreateUserGoogle(profile, response.getAuthResponse().id_token)
      )
      .then((user) => {
        props.closeDialog();
        setProgressVisible(false);
      })
      .catch((err) => {
        setProgressVisible(false);
        props.onError("Bir hata meydana geldi.");
      });
  };

  const onSubmit = (user) => {
    setProgressVisible(true);
    props
      .dispatch(startCreateUserWebApi(user))
      .then((user) => {
        props.closeDialog();
        setProgressVisible(false);
      })
      .catch((err) => {
        setProgressVisible(false);
        props.onError("Bir hata meydana geldi.");
      });
  };

  return (
    <div>
      <Dialog
        open={props.isActive}
        onClose={props.closeDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
        className={classes.dialog}
      >
        <img style={{ width: "100%" }} src="/banner.jpg"></img>
        {progressVisible ? (
          <LinearProgress variant="query" color="secondary" />
        ) : null}
        <DialogContent className={classes.dialogContent}></DialogContent>
      </Dialog>
    </div>
  );
}

export default LoginSignupDialog;
