import {
  signupGoogleApi,
  signupApi,
  userSaveLocal,
  userFetchLocal,
  userRemoveLocal,
  LoginApi,
  UpdateUserApi,
  UpdateUserImageApi
} from "../../Api/userApi";

import { updateUser, removeUser } from "../Actions/userActions";

export const startCreateUserGoogle = (profile, token) => {
  return (dispatch) => {
    return new Promise(function(resolve, reject) {
      signupGoogleApi(profile, token)
        .then((user) => {
          dispatch(updateUser(user));
          userSaveLocal(user);
          resolve(user);
        })
        .catch((err) => {
          console.log(err);
          reject(err);
        });
    });
  };
};

export const startCreateUserWebApi = (user) => {
