import React from "react";
import { Switch, Route, NavLink, Redirect } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import List from "@material-ui/core/List";
import Card from "@material-ui/core/Card";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import SkipPreviousIcon from "@material-ui/icons/SkipPrevious";
import PlayArrowIcon from "@material-ui/icons/PlayArrow";
import SkipNextIcon from "@material-ui/icons/SkipNext";
import { connect } from "react-redux";
import { startRemoveUserLocal } from "../Redux/Selectors/userSelector";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(6),
    marginBottom: theme.spacing(8),
    minHeight: "54vh",
    [theme.breakpoints.down("sm")]: {
      marginTop: theme.spacing(3)
    }
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  Avatar: {
    width: "auto",
    height: 90
  },
  ListItemActive: {
    backgroundColor: theme.palette.action.selected
  },
  card: {
    display: "flex"
  },
  details: {
    display: "flex",
    flexDirection: "column"
  },
  content: {
    flex: "1 0 auto"
  },
  cover: {
    width: 151
  },
  controls: {
    display: "flex",
    alignItems: "center",
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1)
  },
  playIcon: {
    height: 38,
    width: 38
  }
}));
const Account = (props) => {
  const classes = useStyles();
  const theme = useTheme();

  const logoutUser = () => {
    console.log("user logout");
    startRemoveUserLocal(props.dispatch);
    props.history.push("/");
  };

  return (
    <Container className={classes.container} maxWidth="lg">
      <Grid
        direction="row"
        justify="center"
        alignItems="center"
        container
        xs={12}
        spacing={2}
      >
        <Card className={classes.card}>
          <div className={classes.details}>
            <CardContent className={classes.content}>
              <Typography component="h5" variant="h5">
                {props.shoppingCart ? props.shoppingCart[0].title : null}
              </Typography>
              <Typography variant="subtitle1" color="textSecondary">
                {props.shoppingCart
                  ? props.shoppingCart[0].instructor.givenName
                  : null}
              </Typography>
            </CardContent>
            <div className={classes.controls}>
              <IconButton aria-label="previous">
                {theme.direction === "rtl" ? (
                  <SkipNextIcon />
                ) : (
                  <SkipPreviousIcon />
                )}
              </IconButton>
              <IconButton aria-label="play/pause">
                <PlayArrowIcon className={classes.playIcon} />
              </IconButton>
              <IconButton aria-label="next">
                {theme.direction === "rtl" ? (
                  <SkipPreviousIcon />
                ) : (
                  <SkipNextIcon />
                )}
              </IconButton>
            </div>
          </div>
          <CardMedia
            className={classes.cover}
            image={props.shoppingCart ? props.shoppingCart[0].img : null}
            title={props.shoppingCart ? props.shoppingCart[0].title : null}
          />
        </Card>
        <Card className={classes.card}>
          <CardMedia
            className={classes.media}
            image={props.shoppingCart ? props.shoppingCart[0].img : null}
            title={props.shoppingCart ? props.shoppingCart[0].title : null}
          />
          <CardContent>
            <Typography variant="body2" color="textPrimary" component="p">
              {props.shoppingCart ? props.shoppingCart[0].title : null}
            </Typography>
          </CardContent>
        </Card>
      </Grid>
    </Container>
  );
};

const AccountCon = connect((state) => {
  return {
    _id: state.userReducer._id,
    shoppingCart: state.userReducer.shoppingCart
  };
})(Account);

export default AccountCon;
