import React from "react";
import { Player } from "video-react";

const PlyrComponent = (props) => {
  /*
  let player = null;
  React.useEffect(
    (changed) => {
      console.log(props.currentSources);
      if (player) {
        player.source = props.currentSources;
        player.play();
      }
    },
    [props.currentSources]
  );

  React.useEffect(() => {
    console.log("componentDidMount");
    player = new plyr(".js-plyr", props.options);
    player.source = props.sources;

    return () => {
      console.log("componentWillUnmount");
      this.player.destroy();
    };
  }, []);
*/
  return (
    <Player>
      <source src="https://media.w3.org/2010/05/sintel/trailer_hd.mp4" />
    </Player>
  );
};

PlyrComponent.defaultProps = {
  options: {
    controls: [
      "rewind",
      "play",
      "fast-forward",
      "progress",
      "current-time",
      "duration",
      "mute",
      "volume",
      "settings",
      "fullscreen"
    ],
    i18n: {
      restart: "Restart",
      rewind: "Rewind {seektime}s",
      play: "Play",
      pause: "Pause",
      fastForward: "Forward {seektime}s",
      seek: "Seek",
      seekLabel: "{currentTime} of {duration}",
      played: "Played",
      buffered: "Buffered",
      currentTime: "Current time",
      duration: "Duration",
      volume: "Volume",
      mute: "Mute",
      unmute: "Unmute",
      enableCaptions: "Enable captions",
      disableCaptions: "Disable captions",
      download: "Download",
      enterFullscreen: "Enter fullscreen",
      exitFullscreen: "Exit fullscreen",
      frameTitle: "Player for {title}",
      captions: "Captions",
      settings: "Settings",
      menuBack: "Go back to previous menu",
      speed: "Speed",
      normal: "Normal",
      quality: "Quality",
      loop: "Loop"
    }
  },
  sources: {
    type: "video",
    sources: [
      {
        src: "http://localhost:3000/small.mp4",
        type: "video/mp4",
        size: 720
      },
      {
        src: "/path/to/movie.webm",
        type: "video/webm",
        size: 1080
      }
    ]
  }
};

PlyrComponent.propTypes = {
  options: PropTypes.object,
  sources: PropTypes.object,
  source: PropTypes.func,
  destroy: PropTypes.func
};

export default PlyrComponent;
