import { signupGoogleApi, signupApi, userSaveLocal } from "../../Api/userApi";
import ls from "local-storage";

export const updateUser = ({
  _id = "",
  email = "",
  password = "",
  givenName = "",
  familyName = "",
  googleImageUrl = ""
} = {}) => ({
  type: "UPDATE_USER",
  user: {
    _id,
    email,
    password,
    givenName,
    familyName,
    googleImageUrl
  }
});

export const startCreateUserGoogle = (profile, token) => {
  return (dispatch) => {
    return new Promise(function(resolve, reject) {
      signupGoogleApi(profile, token)
        .then((user) => {
          dispatch(updateUser(user));
          userSaveLocal(user);
          resolve(user);
        })
        .catch((err) => {
          console.log(err);
          reject(err);
        });
    });
  };
};

export const startCreateUserWebApi = (user) => {
  return (dispatch) => {
    return new Promise(function(resolve, reject) {
      signupApi(user)
        .then((user) => {
          dispatch(updateUser(user));
          userSaveLocal(user);
          resolve(user);
        })
        .catch((err) => {
          console.log(err);
          reject(err);
        });
    });
  };
};
