import axios from "axios";
import ls from "local-storage";

const url = "http://localhost:4000";

export const userFetchLocal = (dispatch) => {
  dispatch(updateUser(user));
  return ls.get("user");
};

export const userSaveLocal = (user) => {
  return ls.set("user", user);
};

export const signupGoogleApi = (profile, token) => {
  console.log("Signup Api");
  const apiString = url + "/api/user/unauth/create";
  return new Promise((resolve, reject) => {
    axios
      .post(apiString, {
        email: profile.getEmail(),
        givenName: profile.getGivenName(),
        familyName: profile.getFamilyName(),
        googleImageUrl: profile.getImageUrl(),
        googToken: token
      })
      .then((res) => {
        console.log(res.data.user);
        resolve(res.data.user);
      })
      .catch((err) => {
        reject(err);
      });
  });
};

export const signupApi = (user) => {
  console.log("Signup Api");
  const apiString = url + "/api/user/unauth/create";
  return new Promise((resolve, reject) => {
    axios
      .post(apiString, {
        email: user.email,
        givenName: user.givenName,
        familyName: user.familyName,
        password: user.password
      })
      .then((res) => {
        console.log(res.data.user);
        resolve(res.data.user);
      })
      .catch((err) => {
        reject(err);
      });
  });
};
